import React, { useState } from "react"
import { Container, Row, Section, SectionOverlay } from "../../styles/styles"
import { isCatalogCatData, isPrestationSubGroup } from '../../data/CatalogCategoriesData';
import PrestationTabsZero from "./zero/PrestationTabsZero";
import { Content } from "./PrestationElement";
import PrestationTabsStudios from "./studios/PrestationTabsStudos";
import PrestationTabsCommandos from "./commandos/PrestationTabsCommandos";

const CatalogCategories = ({ categories }) =>{

    const [active, setActive] = useState(0);
    const handleClick = e => {
        const index = parseInt(e.target.id, 0);
        if (index !== active) {
        setActive(index);
        }
    };

    return(
        <>
            <Section>
                <SectionOverlay bgcolor="#e5e5e5">
                    <Container>
                        <Row>
                        <div className="catalogCat">
                            {
                                categories.map((cat, index) =>(
                                    <div 
                                        className={`cat${index} ${active === index ? 'isActive' : ''}`}
                                        onClick={handleClick} active={active === index} active={active === index} id={index} key={index}>
                                        <i>
                                            <img 
                                            onClick={handleClick} 
                                            active={active === index} 
                                            id={index}
                                            src={active === index ? cat.icon_orange  : cat.icon}
                                            />
                                        </i>
                                        <h5 onClick={handleClick} active={active === index} id={index}>{cat.title}</h5>
                                    </div>
                                ))
                            }
                        </div>
                        </Row>
                    </Container>
                </SectionOverlay>
            </Section>
            <Content active={active === 0}>
                <PrestationTabsZero {...isPrestationSubGroup}/>
            </Content>
            <Content active={ active === 1}>
                <PrestationTabsStudios {...isPrestationSubGroup}/>
            </Content>
            <Content active={ active === 2 }>
                <PrestationTabsCommandos {...isPrestationSubGroup}/>
            </Content>

        </>
    )
}

export default CatalogCategories
