import React from 'react'
import { PackDiv, Theader, Tbody, Body, Content, TsubHeader } from '../PrestationElement'

export default function SubCategory(props) {
    const { active, subCat, index, handleSelection, subActive } = props;
     
    
    
        return (
                <>
                <TsubHeader
                    //active={active === index}
                    //className={`item${index} ${active === index ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} active={active === index} id={index}
                >
                    {
                     //subCat.id === 1 ?
                     subCat.subCategories.map((cat, index) => (
                        
                        <div
                        className={`item${index} ${active === index ? 'isActive' : ' notActive'}`} active={active === index} id={index}
                        key={index}
                        index={index}
                        onClick={handleSelection}
                        >
                        <h5 active={active === index} id={index}>{cat.name}</h5>
                        
                        </div>
                        
                    )) 
                    
                    
                    }
                    </TsubHeader>
                    
                    <TsubHeader
                    //active={active === index}
                    //className={`item${index} ${active === index ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} active={active === index} id={index}
                >
                    {
                     subCat.id === 25 ?
                     subCat.subCategories.map((cat, index) => (
                        
                             <div
                                className={`item${index} ${subActive === index ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} subActive={subActive === index} id={index}
                                onClick={handleSelection}
                                key={index}
                                index={index}
                            >
                            <h5 subActive={subActive === index} id={index}>{cat.name}</h5>
                            
                            </div>
                        
                    )) 
                    :
                    ''
                    
                    }
                    </TsubHeader>

                    <TsubHeader
                    // active={active === index}
                    className={`item${index} ${active === index ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} active={active === index} id={index}
                >
                    {
                     subCat.id === 50 ?
                     subCat.subCategories.map((cat, index) => (
                        
                             <div
                                className={`item${index} ${subActive === index ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} subActive={subActive === index} id={index}
                                onClick={handleSelection}
                                key={index}
                                index={index}
                            >
                            <h5 subActive={subActive === index} id={index}>{cat.name}</h5>
                            
                            </div>
                        
                    )) 
                    :
                    ''
                    
                    }
                    </TsubHeader>
                </>
        )
    
}
