import styled from "styled-components";

export const Content = styled.div`
    display: flex;
`;

export const BlockRight = styled.div`
    width: 30%;
    background: #ededed;
    padding: 20px;
    .titre{
        background: #ef7422;
        color: #fff;
        padding: 10px 20px;
        text-align: center;
    }

    table{
        td{
            padding: 10px;
        }
    }
`;

export const BlockLeft = styled.div`
    width: 70%;
    .flex{
        display: flex;

        div{
            width: 33%;
            padding: 0 15px;
            &.image{
                display: block;
                text-align: center;
            }
            &.quantity{
                text-align: center;
            }
            &.price{
                text-align: right;
            }
        }
    }
`;
