import React, {useEffect, useState} from "react";
import { Container, Row, Section } from "../../../styles/styles";
import { useParams } from "react-router-dom"
import TopVerticalBar from '../../../features/post/TopVerticalBar';
import { isCatalogueData } from '../../../data/CatalogueData';
import TopMessage from '../TopMessage';
import Title from '../../../features/title/Title';
import { isCatalogueTitle } from '../../../data/TitleData';
import axios from 'axios'
import View from './View'

const ProductView = (props) =>{
    const { item } = props;
    const [ article, getArticle ] = useState('')

    const url ="http://localhost:8000/catalogues";

    useEffect(() => {
        getProductView();
    }, [url])

    const getProductView = () =>{
        axios
        .get(url)
        .then((res) =>{
            const allCatalogues = res.data[0].categories
            getArticle(allCatalogues)
            console.log(allCatalogues)
        })
        .catch((error) => console.error(`Error:${error}`));
    }

    const renderProductView = (cat, index) => (<View/>);

    return(
        <>
        <Title {...isCatalogueTitle}/>
        <TopVerticalBar/>
        <TopMessage {...isCatalogueData}/>
            <Section>
                <Container>
                    <Row>
                    {
                        article.length > 0 ?
                        article.map(renderProductView)
                        : ''
                    }
                    </Row>
                </Container>
            </Section>
        </>
    )
}

export default ProductView
