import React, { useState } from "react";
import ReactHtmlParser from "react-html-parser";
import { isAppMobile, isDecollage, isSecure } from "../../data/packPromoData";
import Button from "../../features/button/Button";
import { Container, Row, Section } from "../../styles/styles";
import { PackDiv, Theader, Tbody, Body, Content } from './PackPromoElement'
import AppMobile from "./promo/AppMobile";
import Decollage from "./promo/Decollage";
import Secure from "./promo/Secure";
import ShoppingCart from "../../images/shopping-cart.svg"

const PackPromo = ({header, packElement, content}) =>{


    const [opened, setOpened] = useState(false);

    const [active, setActive] = useState(0);
    const handleClick = e => {
        const index = parseInt(e.target.id, 0);
        if (index !== active) {
        setActive(index);
        }
    };

    return(
        <>
            <Section>
                <Container>
                    <Row>
                        <div className="titleSection">
                            <h2>{header}</h2>
                        </div>
                    <PackDiv>
                            <Theader>
                                {
                                    packElement.map((item, index) => (
                                        <div key={index} onClick={handleClick} active={active === index} id={index}>
                                            <i><img onClick={handleClick} active={active === index} id={index} src={item.icon}/></i>
                                            <h5 onClick={handleClick} active={active === index} id={index}>{item.head}</h5>
                                        </div>
                                    )) 
                                }
                            </Theader>
                            <Tbody>
                                <Content active={active === 0}>
                                    <Decollage {...isDecollage}/>
                                    <Button className="shoppingButton">
                                        Ajouter au panier <i><img src={ShoppingCart} width="24" height="24" /></i> 
                                    </Button>
                                </Content>
                                <Content active={active === 1}>
                                    <AppMobile {...isAppMobile}/>
                                </Content>
                                <Content active={active === 3}>
                                    <Secure {...isSecure}/>
                                </Content>
                            </Tbody>

                    </PackDiv>
                    </Row>
                </Container>
            </Section>
        </>
    )
}

export default PackPromo;
