import { actionTypes } from "../actions/actionTypes";

export const packsIsLoading = (state = false, action) => {
  switch (action.type) {
    case actionTypes.PACKS_IS_LOADING:
      return action.isLoading;
    default:
      return state;
  }
}; 

export const packs = (state = [], action) => {
  switch (action.type) {
    case actionTypes.PACKS_FETCH_DATA_SUCCESS:
      return action.packs;
    default:
      return state;
  }
};
