import React from 'react'
import { PackDiv, Theader, Tbody, Body, Content } from '../PrestationElement'

export default function SubCategory(props) {
    const { active, subCat, index, handleSelection } = props;
     
    
    
        return (
                <>
                    { subCat.subCategories.map(cat => (
                        <Theader>
                             <div
                                className={`item${index} ${active === index ? 'isActive' : ' notActive'}`} active={active === index} id={index}
                                key={index}
                                index={index}
                                onClick={handleSelection}
                            >
                            <h5 active={active === index} id={index}>{cat.name}</h5>
                            
                            </div>
                        </Theader>
                    ))
                    
                    }
                </>
        )
    
}
