import styled from "styled-components";

export const ItemZone = styled.div``;

export const DivContentBlock = styled.div`
    .itemTabView{
        .tabTitle{
            display: flex;

            div{
                h5{
                    text-transform: uppercase;
                }
            }
        }
    }
`;

export const DivTop = styled.div`
     @media screen and (min-width: 1024px){
        display: flex;
     }
`;

export const DivImg = styled.div`
    display: block;
    text-align: center;
    background: #e5e5e5;
    padding: 7% 0;

    @media screen and (min-width: 1024px){
        width: 30%;
    }
`;

export const DivInfoRight = styled.div`
    display: block;
    text-align: left;

    @media screen and (min-width: 1024px){
        width: 70%;
        padding: 0 20px 0 100px;
    }
    h4{
        text-align: left;
        line-height: 36px;
    }

    div{
        padding-bottom: 25px;

        &.itemDetails{
            table{
                td{
                    padding: 10px 0;
                    &.label{
                        font-weight: 700;
                    }
                }
            }
        }
    }

    button{
        padding: 8px 100px;
        font-size: 16px;
        border: none;
        background: #ef7422;
        color: #fff;
    }
`;
