import React, { useState } from "react";
import ReactHtmlParser from "react-html-parser"
import { Table, Td, TdLeft, TdRight} from "../PrestationElement"

const ProductService = ({ ElementOfPrestion }) => {

    const [tabview, setTabview] = useState(false);

    return(
        <Table>
          
          { 
            ElementOfPrestion.map((item, index) => (
                <div className={`presTabs ${tabview === index ? 'opened' : 'closed'}`} onClick={() =>setTabview(index)} key={index} key={index}>
                        <h5>{item.head}</h5>
                        {
                            tabview === index ? (
                                <>
                                    {
                                        item.content.map((el, index) =>(
                                                                            
                                            <>
                                                <Td key={index}>
                                                    <TdLeft>{ReactHtmlParser(el.title)}</TdLeft>
                                                    <TdRight>{ReactHtmlParser(el.description)}</TdRight>   
                                                </Td>
                                                <Button className="shoppingButton">
                                                    Ajouter au panier <i><img src={ShoppingCart} width="24" height="24" /></i> 
                                                </Button>
                                            </>
                                                                            
                                        ))
                                    }
                                </>
                            ) : ''
                        }
                    </div>
            ))
            }
        </Table>
    )
}

export default ProductService
