import React from 'react'
import { PackDiv, Theader, Tbody, Body, Content, TsubHeader } from '../PrestationElement'

export default function SubCategory(props) {
    const { active, subCat, index, handleSelection, subActive } = props;
     
    
    
        return (
                <>
                <TsubHeader
                    active={active === subCat.id}
                    className={`item${subCat.id} ${active === subCat.id ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} active={active === subCat.id} id={subCat.id}
                >
                    {
                     //subCat.id === 1 ?
                     subCat.subCategories.map((cat, index) => (
                        
                        <div
                        className={`item${index} ${subActive === index ? 'isActive' : ' notActive'}`} subActive={subActive === index} id={index}
                        //key={index}
                        index={index}
                        onClick={handleSelection}
                        >
                        <h5 active={subActive === index} id={index}>{cat.name}</h5>
                        
                        </div>
                        
                    )) 
                    
                    
                    }
                    </TsubHeader>
                    
                    
                </>
        )
    
}
