import React from 'react'
import { PackDiv, Theader, Tbody, Body, Content, TsubHeader } from '../PrestationElement'

export default function SubCategory(props) {
    const { active, subCat, index, subIndex, handleSelection, subActive } = props;
     
    
    
        return (
                <>
                <TsubHeader
                    active={active === index}
                >
                    { subCat.subCategories.map(cat => (
                        
                             <div
                                className={`item${index} ${subActive === subIndex ? 'isActive' : ' notActive'} ${subCat.subCategories.length === 2 ? 'twoColumns': ''}`} active={subActive === index} id={index}
                                onClick={handleSelection}
                                key={subIndex}
                                index={subIndex}
                            >
                            <h5 active={subActive === index} id={index}>{cat.name}</h5>
                            
                            </div>
                        
                    ))
                    
                    }
                    </TsubHeader>
                </>
        )
    
}
