import { createSlice } from '@reduxjs/toolkit';


const initialState = {
    basketData: []
}

console.log(initialState)

const BasketSlice = createSlice({
    name: "baskets",
    initialState,
    reducers: {
    addToBasket : (state, action) =>{
        state.basketData.push(action.payload)
    },

    removeFromBasket: (state, action) =>{
        const index = state.basketData.findIndex(
            (basketItem) => basketItem.index === action.payload.index
        );
        let newBasket = [...state.basketData];

        if (index >= 0) {
            newBasket.splice(index);

          } else {
            console.warn(
              `Cant remove product (id: ${action.payload.index}) as its not in basket!`
            )
        }
        return {
            ...state,
            basketData: newBasket
        }
    }



    }
});

export const {
    addToBasket,
    removeFromBasket
} = BasketSlice.actions

export const selectBasketData = state => state.baskets.basketData;

export default BasketSlice.reducer
