import React, { useState } from "react";
import ReactHtmlParser from "react-html-parser";
import { isAppMobile, isDecollage, isSecure } from "../../data/packPromoData";
import Button from "../../features/button/Button";
import { Container, Row, Section } from "../../styles/styles";
import { PackDiv, Theader, Tbody, Body, Content } from './PrestationElement'
import Client from "./prestation/client"
import ProductService from "./prestation/ProductService"
import MarcheConcurrence from "./prestation/MarcheConcurrence"
import StrategieMarketing from "./prestation/StrategieMarketing"
import ShoppingCart from "../../images/shopping-cart.svg"

const PrestationTabs = ({header, prestElement, content}) =>{


    const [opened, setOpened] = useState(false);

    const [active, setActive] = useState(0);
    const handleClick = e => {
        const index = parseInt(e.target.id, 0);
        if (index !== active) {
        setActive(index);
        }
    };

    return(
        <>
            <Section>
                <Container>
                    <Row>
                        <div className="titleSection">
                            <h5>{header}</h5>
                        </div>
                    <PackDiv>
                            <Theader>
                                {
                                    prestElement.map((item, index) => (
                                        <div className={`item${index} ${active === index ? 'isActive' : ''}`} key={index} onClick={handleClick} active={active === index} id={index}>
                                            <i>
                                                <img 
                                                    onClick={handleClick} 
                                                    active={active === index} 
                                                    id={index} 
                                                    src={active === index ? item.icon_white  : item.icon}
                                                />
                                            </i>
                                            <h5 onClick={handleClick} active={active === index} id={index}>{item.head}</h5>
                                        </div>
                                    )) 
                                }
                            </Theader>
                            <Tbody>
                                <Content active={active === 0}>
                                    <Decollage {...isDecollage}/>
                                    <Button className="shoppingButton">
                                        Ajouter au panier <i><img src={ShoppingCart} width="24" height="24" /></i> 
                                    </Button>
                                </Content>
                                <Content active={active === 1}>
                                    <AppMobile {...isAppMobile}/>
                                </Content>
                                <Content active={active === 3}>
                                    <Secure {...isSecure}/>
                                </Content>
                            </Tbody>
                        
                    </PackDiv>
                    </Row>
                </Container>
            </Section>
        </>
    )
}

export default PrestationTabs;
