import styled from "styled-components";
import Puce from "../../images/puce.png"

export const PackDiv = styled.div`
    margin: 50px 0;
`;

export const Theader = styled.div`
    display: flex;
    div{
        width: 33.3333%;
        text-align: center;
        padding: 20px;
        cursor: pointer;
        //background: ${props =>props.active === props.index ? "#000" : "#e5e5e5"};
        background: #fdf1e8;

        &.isActive{
            background: #ef7422;
            color: #fff;
        }

        &.item1{
            margin: 0 0 0 5px;
        }
        &.item2{
            margin: 0 5px;
        }
        
    }
`;
export const Tbody = styled.div`
    
    .presTabs{
        border: 2px solid #ef7422;
        padding: 20px;
        margin-top: 5px;

        h5{
            text-align: center;
        }

        &.closed{
            background: #ef7422;
            h5{
                color: #fff;
            }
        }
        &.opened{
            h5{
                color: #ef7422;
                //border-bottom: 1px solid #000;
                padding: 20px;
            }
        }
    }
`;

export const Body = styled.div`
`;

export const Content = styled.div`
  ${props => (props.active ? "" : "display:none")};

  a.shoppingButton{
      max-width: 100px;
      margin: 30px auto;
      padding: 5px;
      color: #000;
      font-weight: 600;
      border: 1px solid #ef7422;
      
  }
`;

export const Table = styled.div``;

export const TdLeft = styled.div`
    width: 30%;
    text-align: right;
    padding-right: 80px;
`;

export const TdRight = styled.div`
    width: 70%;
`;

export const Td = styled.div`
    display: flex;

    padding: 30px 10px;
    border-top: 1px solid #000;

    ul{
        list-style: none;
    }
                
    .ulElement{
        display: flex;
        ul{
            padding: 0 20px;
            list-style: none;
            //background: url(${Puce}) left 5px no-repeat;
            position: relative;

            &:before{
                content: url(${Puce});
                position: absolute;
                left: 0;
            }
            
            li{
                ul{
                    margin-top: 50px;
                    padding: 0 !important;
                    &:before{
                        left: -20px;
                    }
                }
            }
        }
    }
    
    &:last-child{
        border: 0;
        margin-bottom: 50px;
    }

    table{
        td{
            padding: 10px;
        }
    }
`;
