import { actionTypes } from "./actionTypes"

const product = '';

export const addProductToCart = (product) =>{
    return{
        type: actionTypes.ADD_TO_CART,
        product
    };
}

export const removeProductFromCart = index =>{
    return{
        type: actionTypes.REMOVE_FROM_CART,
        product
    }
}
